const unsigned char color = 1;

void LCD_Rectangle(int x, int y, int b, int a)
{
  int j; // zmienna pomocnicza
  // rysowanie linii pionowych (boki)
  for (j = 0; j < a; j++) {
		LCD_SetPixel(x, y + j, color);
		LCD_SetPixel(x + b - 1, y + j, color);
	}
  // rysowanie linii poziomych (podstawy)
  for (j = 0; j < b; j++)	{
		LCD_SetPixel(x + j, y, color);
		LCD_SetPixel(x + j, y + a - 1, color);
	}
}


void LCD_Circle(int cx, int cy ,int radius)
{
  int x, y, xchange, ychange, radiusError;
  x = radius;
  y = 0;
  xchange = 1 - 2 * radius;
  ychange = 1;
  radiusError = 0;
  while(x >= y)
    {
      LCD_SetPixel(cx+x, cy+y, color); 
      LCD_SetPixel(cx-x, cy+y, color); 
      LCD_SetPixel(cx-x, cy-y, color);
      LCD_SetPixel(cx+x, cy-y, color); 
      LCD_SetPixel(cx+y, cy+x, color); 
      LCD_SetPixel(cx-y, cy+x, color); 
      LCD_SetPixel(cx-y, cy-x, color); 
      LCD_SetPixel(cx+y, cy-x, color); 
      y++;
      radiusError += ychange;
      ychange += 2;
      if ( 2*radiusError + xchange > 0 )
      {
        x--;
        radiusError += xchange;
        xchange += 2;
    	}
    }
}


void LCD_Line(int X1, int Y1,int X2,int Y2)
{
  int CurrentX, CurrentY, Xinc, Yinc, 
      Dx, Dy, TwoDx, TwoDy, 
  	TwoDxAccumulatedError, TwoDyAccumulatedError;

  Dx = (X2-X1); // obliczenie skadowej poziomej
  Dy = (Y2-Y1); // obliczenie skadowej pionowej

  TwoDx = Dx + Dx; // podwojona skadowa pozioma
  TwoDy = Dy + Dy; // podwojona skadowa pionowa

  CurrentX = X1; // zaczynamy od X1
  CurrentY = Y1; // oraz Y1

  Xinc = 1; // ustalamy krok zwikszania pozycji w poziomie 
  Yinc = 1; // ustalamy krok zwikszania pozycji w pionie

  if(Dx < 0) // jesli skadowa pozioma jest ujemna 
    {
    Xinc = -1; // to bdziemy si "cofa" (krok ujemny)
    Dx = -Dx;  // zmieniamy znak skadowej na dodatni
    TwoDx = -TwoDx; // jak rwnie podwojonej skadowej
    }

  if (Dy < 0) // jeli skadowa pionowa jest ujemna
    {
    Yinc = -1; // to bdziemy si "cofa" (krok ujemny)
    Dy = -Dy; // zmieniamy znak skadowej na dodatki
    TwoDy = -TwoDy; // jak rwniez podwojonej skadowej
    }

  LCD_SetPixel(X1,Y1, color); // stawiamy pierwszy krok (zapalamy pierwszy piksel)

  if ((Dx != 0) || (Dy != 0)) // sprawdzamy czy linia skada si z wicej ni jednego punktu ;)
    {
    // sprawdzamy czy skadowa pionowa jest mniejsza lub rwna skadowej poziomej
    if (Dy <= Dx) // jeli tak, to idziemy "po iksach"
      { 
      TwoDxAccumulatedError = 0; // zerujemy zmienn 
      do // ruszamy w drog
  	  {
        CurrentX += Xinc; // do aktualnej pozycji dodajemy krok 
        TwoDxAccumulatedError += TwoDy; // a tu dodajemy podwojon skadow pionow
        if(TwoDxAccumulatedError > Dx)  // jeli TwoDxAccumulatedError jest wikszy od Dx
          {
          CurrentY += Yinc; // zwikszamy aktualn pozycj w pionie
          TwoDxAccumulatedError -= TwoDx; // i odejmujemy TwoDx
          }
         LCD_SetPixel(CurrentX,CurrentY, color);// stawiamy nastpny krok (zapalamy piksel)
         }while (CurrentX != X2); // idziemy tak dugo, a osigniemy punkt docelowy
       }
     else // w przeciwnym razie idziemy "po igrekach" 
        {
        TwoDyAccumulatedError = 0; 
        do 
  	    {
          CurrentY += Yinc; 
          TwoDyAccumulatedError += TwoDx;
          if(TwoDyAccumulatedError>Dy) 
            {
            CurrentX += Xinc;
            TwoDyAccumulatedError -= TwoDy;
            }
           LCD_SetPixel(CurrentX,CurrentY, color); 
           }while (CurrentY != Y2);
      }
    }
}


int GetCharWidth(const char character) {

  int fontIndex, fontPos = 0;
  
  fontIndex = character - 32;
  if ( character < 32 ) return 0;
  if ( fontIndex > 96 ) return 0;
  
  if ( fontIndex > 0 ) {
    fontPos = Font5x5WidthPos[ fontIndex - 1];
  }
  return (Font5x5WidthPos[ fontIndex ] - fontPos);

}

/*
* Draw a Char 5x5
*/
int DrawChar5x5(int x, int y, const char character, int color, int bgcolor) {
  int i, j, fontWidth, fontPos = 0, fontIndex;  
  
  fontWidth = GetCharWidth(character);
  
  if (fontWidth == 0) return 0;
  
  fontIndex = character - 32;
  
  if ( fontIndex > 0 ) {
    fontPos = Font5x5WidthPos[ fontIndex - 1];
  }
  
  //LCD_SetArea(x, y, x + fontWidth + 1, y + 5);
  //LCD_SendCMD(RAMWR);
  for (i = 0; i < fontWidth; i++) {
    for (j = 0; j <= 5; j++) {      
      if ( ValBit( Font5x5[ fontPos + i ], j ) ) {        
        //LCD_SendDAT16(color);
        LCD_SetPixel(x + i, y + j, color); 
      } else {
        //LCD_SendDAT16(bgcolor);
        LCD_SetPixel(x + i, y + j, bgcolor); 
      }
    }    
  }
  //for (j = 0; j <= 5; j++) { 
  //  LCD_SendDAT16(bgcolor);
  //}
  fontWidth++;
  return fontWidth;
}

/*
* Draw a Char 16
*/
int DrawChar16(int x, int y, const char character, int color, int bgcolor) {
  int i, j, b, fontWidth, fontHight, fontIndex, nextChar = 0;  
  
  fontWidth = 16;
  fontHight = 4;

  fontIndex = (character - 32) * ((fontWidth * fontHight) + 1);
  nextChar = Droid_Sans_Mono16x27[fontIndex] + 2;
  fontIndex =  fontIndex + 1;
  for (i = 0; i < fontWidth; i++) {
    for (j = 0; j < fontHight; j++) {
      for (b = 0; b < 8; b++) {
        if ( ValBit( Droid_Sans_Mono16x27[ fontIndex + j + (i * fontHight) ], b ) ) {
          LCD_SetPixel(x + i, y + (j * 8) + b, color);
        }
      }
    }
  }
  return nextChar;
}

