void Puts(int x, int y, const char *s) {
  char c;
  
  while(*s) {
    c = *s++;
    if(c >= 0x20) {
        x = x + DrawChar5x5(x, y, c, 1, 0);
    }
  }
    
}

void WriteText(int x, int y, const char *s, int size) {
  char c;
  
  while(*s) {
    c = *s++;
    if(c >= 0x20) {
        if (size == 1)
           x = x + DrawChar11(x, y, c, 1, 0);
        else if (size == 2)
           x = x + DrawChar16(x, y, c, 1, 0);
        else if (size == 3)
           x = x + DrawCharMet(x, y, c, 1, 0);
    }   
  }

}